import  { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import { loginSuccess } from '../features/auth/authSlice';

export default function Login() {
  const [email, setEmail] = useState('admin@hotel.com');
  const [password, setPassword] = useState('admin123');
  const [error, setError] = useState('');

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {  

      const response = await axios.post('http://localhost:8080/api/auth/login', { email, password });

      localStorage.setItem('token', response.data.token);
      localStorage.setItem('refreshToken', response.data.refreshToken);
      dispatch(loginSuccess(response.data));

      const from = location.state?.from?.pathname;
      if (from) {
        navigate(from, { replace: true });
        return;
      }

      if (res.user.role === 'ADMIN') {
        navigate('/admin', { replace: true });
        return;
      }

      navigate('/dashboard', { replace: true });
    } catch (err) {
       setError(err?.message || 'Login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 dark:bg-gray-950">
      <div className="mx-auto max-w-md rounded-xl border border-gray-200 bg-white p-6 shadow-sm dark:border-gray-800 dark:bg-gray-900">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Login</h2>

        {error ? <div className="mt-2 text-sm text-red-600">{error}</div> : null}

        <form onSubmit={onSubmit} className="mt-4 space-y-3">
          <div>
            <label className="text-sm text-gray-700 dark:text-gray-200">Email</label>
            <input
              className="mt-1 w-full rounded-md border border-gray-200 bg-white px-3 py-2 text-sm outline-none dark:border-gray-800 dark:bg-gray-950 dark:text-gray-100"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
          </div>

          <div>
            <label className="text-sm text-gray-700 dark:text-gray-200">Password</label>
            <input
              className="mt-1 w-full rounded-md border border-gray-200 bg-white px-3 py-2 text-sm outline-none dark:border-gray-800 dark:bg-gray-950 dark:text-gray-100"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>

          <button
            className="w-full rounded-md bg-blue-600 px-3 py-2 text-sm font-semibold text-white hover:bg-blue-700"
            type="submit"
          >
            Login
          </button>
        </form>

        <div className="mt-4 text-sm text-gray-700 dark:text-gray-200">
          No account? <Link className="text-blue-600 hover:underline" to="/register">Register</Link>
        </div>

        <div className="mt-4 text-xs text-gray-600 dark:text-gray-300">
          Admin demo:
          <div>
            <code>admin@hotel.com</code> / <code>admin123</code>
          </div>
        </div>
      </div>
    </div>
  );
}
