import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

import { api } from '../../api';

const initialState = {
  rows: [],
  page: 1,
  pageSize: 5,
  total: 0,
  status: 'idle',
  error: null,
};

export const fetchUsers = createAsyncThunk('users/fetch', async ({ page, pageSize }) => {
  const res = await api.users.list({ page, pageSize });
  return res;
});

export const addUserThunk = createAsyncThunk('users/add', async ({ name, email, password, role }) => {
  const res = await api.users.add({ name, email, password, role });
  return res;
});

const usersSlice = createSlice({
  name: 'users',
  initialState,
  reducers: {
    setPage: (state, action) => {
      state.page = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchUsers.pending, (state) => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(fetchUsers.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.rows = action.payload.data;
        state.total = action.payload.total;
      })
      .addCase(fetchUsers.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(addUserThunk.fulfilled, (state) => {
        state.status = 'succeeded';
      });
  },
});

export const { setPage } = usersSlice.actions;
export default usersSlice.reducer;
