import  { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import Table from '../../components/common/Table';
import Pagination from '../../components/common/Pagination';

export default function Users() {
  const dispatch = useDispatch();
  const { rows, page, pageSize, total, status, error } = useSelector((s) => s.users);

  const totalPages = Math.max(1, Math.ceil(total / pageSize));

  useEffect(() => {
    dispatch(fetchUsers({ page, pageSize }));
  }, [dispatch, page, pageSize]);

  const columns = [
    { key: 'name', header: 'Name' },
    { key: 'email', header: 'Email' },
    { key: 'role', header: 'Role' },
  ];

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Users</h2>
        <div className="text-sm text-gray-600 dark:text-gray-300">
          Total: <span className="font-semibold">{total}</span>
        </div>
      </div>

      {error ? <div className="text-sm text-red-600">{error}</div> : null}

      {status === 'loading' ? (
        <div className="text-sm text-gray-600 dark:text-gray-300">Loading...</div>
      ) : rows.length ? (
        <Table columns={columns} rows={rows} />
      ) : (
        <div className="text-sm text-gray-600 dark:text-gray-300">No users</div>
      )}

      <Pagination
        page={page}
        totalPages={totalPages}
        onPrev={() => dispatch(setPage(Math.max(1, page - 1)))}
        onNext={() => dispatch(setPage(Math.min(totalPages, page + 1)))}
      />
    </div>
  );
}
