import { createSlice } from '@reduxjs/toolkit';

const initialState = {
  user: null,
  token: null,
  refreshToken: null,
  status: 'idle',
  error: null,
};

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {

    bootSuccess: (state, action) => {
      state.status = 'succeeded';
      state.user = action.payload?.user ?? null;
      state.token = action.payload?.token ?? null;
      state.refreshToken = action.payload?.refreshToken ?? null;
      state.error = null;
    },
    loginSuccess: (state, action) => {
      state.status = 'succeeded';
      state.user = action.payload?.user ?? null;
      state.token = action.payload?.token ?? null;
      state.refreshToken = action.payload?.refreshToken ?? null;
      state.error = null;
    },
    registerSuccess: (state) => {
      state.status = 'succeeded';
      state.error = null;
    },
    logoutSuccess: (state) => {
      state.user = null;
      state.token = null;
      state.refreshToken = null;
      state.status = 'idle';
      state.error = null;
    },
  },
});

export const { authStart, authFail, bootSuccess, loginSuccess, registerSuccess, logoutSuccess } =
  authSlice.actions;

export default authSlice.reducer;
