import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';

import { api } from '../../api';

const initialState = {
  user: null,
  token: localStorage.getItem('token') || null,
  status: 'idle',
  error: null,
};

export const bootAuth = createAsyncThunk('auth/boot', async () => {
  const user = await api.auth.me();
  const token = localStorage.getItem('token') || null;
  return { user, token };
});

export const loginThunk = createAsyncThunk('auth/login', async ({ email, password }) => {
  const res = await api.auth.login({ email, password });
  return res;
});

export const registerThunk = createAsyncThunk('auth/register', async ({ name, email, password }) => {
  const user = await api.auth.register({ name, email, password });
  return user;
});

export const logoutThunk = createAsyncThunk('auth/logout', async () => {
  await api.auth.logout();
  return true;
});

const authSlice = createSlice({
  name: 'auth',
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(bootAuth.pending, (state) => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(bootAuth.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.user = action.payload.user;
        state.token = action.payload.token;
      })
      .addCase(bootAuth.rejected, (state, action) => {
        state.status = 'failed';
        state.user = null;
        state.token = null;
        state.error = action.error.message;
      })
      .addCase(loginThunk.pending, (state) => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(loginThunk.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.user = action.payload.user;
        state.token = action.payload.token;
      })
      .addCase(loginThunk.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(registerThunk.pending, (state) => {
        state.status = 'loading';
        state.error = null;
      })
      .addCase(registerThunk.fulfilled, (state) => {
        state.status = 'succeeded';
      })
      .addCase(registerThunk.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.error.message;
      })
      .addCase(logoutThunk.fulfilled, (state) => {
        state.user = null;
        state.token = null;
        state.status = 'idle';
        state.error = null;
      });
  },
});

export default authSlice.reducer;
