import  { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import axios from 'axios';
import { loginSuccess } from '../store/features/authSlice';

export default function Login() {
  const [username, setUserName] = useState('shakib410');
  const [password, setPassword] = useState( 'SecurePassword123!');
  const [error, setError] = useState('');

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  const [otp, setOtp] = useState('');

   const handleGenerateOtp = async (e) => {
    e.preventDefault();
    setError('');

    try {  

      const response = await axios.post('http://localhost:8080/api/auth/generate-otp?username=' + username);




    } catch (err) {
       setError(err?.message || 'Login failed');
    }
  };

  const handleVerifyOtp = async (e) => {
    e.preventDefault();
    setError('');

    try {  

      const response = await axios.post('http://localhost:8080/api/auth/generate-otp?username=' + username);

      sessionStorage.setItem('token', response?.data?.accessToken);
      sessionStorage.setItem('refreshToken', response?.data?.refreshToken);
      dispatch(loginSuccess(response?.data));



      const from = location.state?.from?.pathname;
      if (from) {
        navigate(from, { replace: true });
        return;
      }

      if (response?.data?.user?.role === 'ADMIN') {
        navigate('/admin', { replace: true });
        return;
      }else {
        navigate('/dashboard', { replace: true });
        return;
      }

    } catch (err) {
       setError(err?.message || 'Login failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-6 dark:bg-gray-950">
      <div className="mx-auto max-w-md rounded-xl border border-gray-200 bg-white p-6 shadow-sm dark:border-gray-800 dark:bg-gray-900">
        <h2 className="text-xl font-semibold text-gray-900 dark:text-gray-100">Login</h2>

        {error ? <div className="mt-2 text-sm text-red-600">{error}</div> : null}

        <form className="mt-4 space-y-3">
          <div>
            <label className="text-sm text-gray-700 dark:text-gray-200">User Name</label>
            <input
              className="mt-1 w-full rounded-md border border-gray-200 bg-white px-3 py-2 text-sm outline-none dark:border-gray-800 dark:bg-gray-950 dark:text-gray-100"
              value={username}
              onChange={(e) => setUserName(e.target.value)}
            />
          </div>

          <div>
            <label className="text-sm text-gray-700 dark:text-gray-200">OTP</label>
            <input
              className="mt-1 w-full rounded-md border border-gray-200 bg-white px-3 py-2 text-sm outline-none dark:border-gray-800 dark:bg-gray-950 dark:text-gray-100"
              type="password"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
            />
          </div>

          <button
            className="w-full rounded-md bg-blue-600 px-3 py-2 text-sm font-semibold text-white hover:bg-blue-700"
            onClick={handleGenerateOtp}
          >
            Generate OTP
          </button>

          <button
            className="w-full rounded-md bg-blue-600 px-3 py-2 text-sm font-semibold text-white hover:bg-blue-700"
            onClick={handleVerifyOtp}
          >
            Verify OTP
          </button>
        </form>

        <div className="mt-4 text-sm text-gray-700 dark:text-gray-200">
          No account? <Link className="text-blue-600 hover:underline" to="/register">Register</Link>
        </div>


      </div>
    </div>
  );
}
