import React, { createContext, useContext, useMemo } from 'react';
import { useDispatch, useSelector } from 'react-redux';

import { api } from '../api';
import { authFail, authStart, loginSuccess, logoutSuccess, registerSuccess } from '../features/auth/authSlice';

const AuthContext = createContext(null);

export function AuthProvider({ children }) {
  const dispatch = useDispatch();
  const user = useSelector((s) => s.auth.user);
  const status = useSelector((s) => s.auth.status);
  const booting = status === 'loading';

  const value = useMemo(
    () => ({
      user,
      booting,
      login: async ({ email, password }) => {
        try {
          dispatch(authStart());
          const res = await api.auth.login({ email, password });
          localStorage.setItem('token', res.token);
          localStorage.setItem('refreshToken', res.refreshToken);
          dispatch(loginSuccess(res));
          return res;
        } catch (err) {
          dispatch(authFail(err?.message || 'Login failed'));
          throw err;
        }
      },
      register: async ({ name, email, password }) => {
        try {
          dispatch(authStart());
          const res = await api.auth.register({ name, email, password });
          dispatch(registerSuccess(res));
          return res;
        } catch (err) {
          dispatch(authFail(err?.message || 'Register failed'));
          throw err;
        }
      },
      logout: async () => {
        try {
          dispatch(authStart());
          await api.auth.logout();
          localStorage.removeItem('token');
          localStorage.removeItem('refreshToken');
          dispatch(logoutSuccess());
        } catch (err) {
          dispatch(authFail(err?.message || 'Logout failed'));
          throw err;
        }
      },
    }),
    [dispatch, user, booting]
  );

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  return useContext(AuthContext);
}
